<?php

namespace AppBundle\Repository;

/**
 * ChequeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ChequeRepository extends \Doctrine\ORM\EntityRepository
{
	public function findByTexto($texto) {
        $query = 'SELECT c  FROM AppBundle:Cheque c ';
        
        if ($texto != '')
            $query .= ' WHERE c.numero LIKE :texto';

        $query .= ' ORDER BY c.fecha DESC ';

        $em = $this->getEntityManager()->createQuery($query);

        if ($texto != '')
            $em->setParameter('texto','%' . $texto . '%');
        return $em;
    }
}
